import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React, { useEffect, useMemo, useState } from 'react';
import { Login } from './components/login/login';
import { User } from './types/user';
import { FontAwesomeLibrary } from './util/font-awesome-library';
import { useStore } from './stores';
import { ProductCard } from './components/product-card/product-card';

import './shop.scss';

interface InCartProducts {
    [productId: number]: number;
}

enum MODE {
    CATALOGE,
    CART,
}

export const Shop = () => {
    const store = useStore();
    const productStore = store.productStore;
    const products = productStore.products;

    const [loggedIn, setLoggedIn] = useState<boolean>(false);
    const [inCartProducts, setInCartProducts] = useState<InCartProducts>({4: 3});
    const [mode, setMode] = useState<MODE>(MODE.CATALOGE);

    useEffect(
        () => {
            FontAwesomeLibrary.initializeFontAwesomeLibrary();
        },
        [],
    );

    useEffect(
        () => {
            if (mode === MODE.CATALOGE) {
                productStore.index();
            }
        },
        [mode, productStore],
    )

    const inCartProductCount = useMemo(
        () => {
            return Object.values(inCartProducts).reduce(
                (count, productCount) => {
                    return count + productCount;
                },
                0,
            );
        },
        [inCartProducts],
    )

    const handleSignIn = (user: User, isNew: boolean) => {
        // FIXME:
        setLoggedIn(true);
    }

    const handleSignOut = () => {
        setLoggedIn(false);
    }

    return (
        <div className="shop">
            <Login
                loggedIn={loggedIn}
                onSignIn={handleSignIn}
            />
            <div className={`manager ${loggedIn ? 'shown' : ''}`}>
                <div className="navbar">
                    <div className="brand">
                        shop<span className="secondary">.io</span>
                    </div>
                    <div className="actions">
                        <div className={`action ${mode === MODE.CATALOGE ? 'selected' : ''}`}>
                            <FontAwesomeIcon icon="store"/>
                            <div className="selected-indicator"/>
                        </div>
                        <div className={`action ${mode === MODE.CART ? 'selected' : ''}`}>
                            <FontAwesomeIcon icon="shopping-cart"/>
                            <div className={`counter ${inCartProductCount > 0 ? 'shown' : ''}`}>
                                {inCartProductCount}
                            </div>
                            <div className="selected-indicator"/>
                        </div>
                        <div
                            className="action"
                            style={{ fontSize: 18 }}
                            onClick={handleSignOut}
                        >
                            <FontAwesomeIcon icon="sign-out-alt"/>
                            <div className="selected-indicator"/>
                        </div>
                    </div>
                </div>
                <div className="catalogue">
                    {products.map(product => (
                        <ProductCard
                            key={`product-${product.id}`}
                            product={product}
                        />
                    ))}
                </div>
            </div>
        </div>
    );
};
